			for(var i = startPoint.line; i <= endPoint.line; i++) {
				(function(i) {
					var text = cm.getLine(i);
					if(stat[name]) {
						text = text.replace(repl[name], "$1");
					} else {
						text = map[name] + text;
					}
					cm.replaceRange(text, {
						line: i,
						ch: 0
					}, {
						line: i,
						ch: 99999999999999
					});
				})(i);
			}
			cm.focus();
